com.mirth.connect.plugins.javascriptrule.script:
// get the patient ID from the repeated PID 3 fieldfor each (child in msg['PID']['PID.3']) {	if (child['PID.3.5'].toString() == 'MR' && child['PID.3.3'].toString() == 'ISO') {		var patientid = child['PID.3.1'].toString();
	}
}
if (!patientid) {
	logger.info('REF: HDWA Patient ID not found');
	return false;}
logger.info("REF: Patient ID = " + patientid);//SQL to get the RecordNumvar sql = "SELECT recordnum from Debtor4 where code = '" + patientid.toString() + "' and deleted = 0";var results = SQLSelect(sql);if (results.size() == 0) {	logger.info("REF: No such patient " + patientid.toString() + ", adding...");	// Get the next recordnum for a new Debtor4 record	var sql = "declare @r varchar(25);exec spSpark_GetNextRecordNum 'DEBTOR4',@recordnum = @r output;select @r as nextRecordNum;";	var results = SQLSelect(sql);	results.next(); // executeCachedQuery starts at row 0 of the ResultSet - so we advance to row 1	var nextRecordNum = results.getString(1);	logger.info("REF: NextDebtorRecordNum = " + nextRecordNum);	channelMap.put('Next Debtor Record Number',nextRecordNum);	var patientRN = nextRecordNum;
	logger.info("REF: Patient RecordNum = " + patientRN);	channelMap.put('Patient RecordNum' , patientRN);	channelMap.put('Patient code' , patientid.toString());	return true;} else {	results.next(); // The ResultSet starts at row 0 - so we advance to row 1	var patientRN = results.getString('RecordNum');
	logger.info("REF: Patient Recordnum " + patientRN + " already exists");	channelMap.put('Patient RecordNum' , patientRN);	channelMap.put('Patient code' , patientid.toString());
	return false;}

