com.mirth.connect.plugins.javascriptstep.script:
var patientRN = $('Patient RecordNum');
var URL = 'https://WSIO123APP.hdwa.health.wa.gov.au/Titanium/Patient.svc/ajaxEndpoint/PATIENTS/UpdatePatientSearchIndex/' + patientRN.replace(":","_");
channelMap.put('PatientURL','URL')

var sql = "select top 1 deleted from debtor4 where code = 'B1234566' order by deleted desc";

var results = SQLSelect(sql);

if (results.next()) { // executeCachedQuery starts at row 0 of the ResultSet - so we advance to row 1
	var deleted = results.getString('deleted') + 1;	// deleted record exists
} else { // the appointment doesn't exist - a new one must be created
	var deleted = 1; // no deleted record
}

channelMap.put('PatientDeleted',deleted);

com.mirth.connect.plugins.javascriptrule.script:
// get the patient ID from the repeated PID 3 fieldfor each (child in msg['PID']['PID.3']) {	if (child['PID.3.5'].toString() == 'MR' && child['PID.3.4'].toString() == 'HDWA') {		var patientid = child['PID.3.1'].toString();	}
}

if (!patientid) {
	logger.info('REF: HDWA Patient ID not found');
	return false;}
logger.info("REF: Patient ID = " + patientid);//SQL to get the RecordNumvar sql = "SELECT recordnum from Debtor4 where code = '" + patientid.toString() + "' and deleted = 0";var results = SQLSelect(sql);if (results.size() == 0) { // patient not in Titanium	logger.info("REF: No such patient " + patientid.toString() + ", no action taken.");	return false;} else { // patient exists in Titanium	results.next(); // The ResultSet starts at row 0 - so we advance to row 1	var patientRN = results.getString('RecordNum');
	logger.info("REF: Patient Recordnum " + patientRN + " exists, ");	channelMap.put('Patient RecordNum' , patientRN);	channelMap.put('Patient code' , patientid.toString());
	// now we need to check if they have an appointment
	var sql = "SELECT recordnum,ridPatient,ssExternalID from APPT6 where ridPatient = '" + patientRN + "' and deleted = 0 and wStatus = 1";
	var results = SQLSelect(sql);
	if (results.next()) { // executeCachedQuery starts at row 0 of the ResultSet - so we advance to row 1
		var apptID = results.getString('ssExternalID'); // the appointment already exists in the DB - don't remove the patient
		return false;
	} else { // no appointment - we can remove the patient
		logger.info("REF: No appointment - remove patient from Titanium");
		return true;
	}
	return false;}

