com.mirth.connect.plugins.javascriptstep.script:
var sql = "declare @r varchar(25);exec spSpark_GetNextRecordNum 'APPT6',@recordnum = @r output;select @r as nextRecordNum;";

var results = SQLSelect(sql);

results.next(); // executeCachedQuery starts at row 0 of the ResultSet - so we advance to row 1

var nextRecordNum = results.getString(1);
logger.info("SIU: NextRecordNum = " + nextRecordNum);

channelMap.put('Next Record Number',nextRecordNum);

var msgApptID = msg['SCH']['SCH.1']['SCH.1.1'].toString();
logger.info("SIU: External ID from message = " + msgApptID );

var sql = "SELECT recordnum,ssExternalID from APPT6 where ssExternalID = '" + msgApptID + "'";

var results = SQLSelect(sql);

if (results.next()) { // executeCachedQuery starts at row 0 of the ResultSet - so we advance to row 1
	var apptID = results.getString('ssExternalID'); // the appointment already exists in the DB
	var recordNum = results.getString('RecordNum');
	channelMap.put("Appointment ID",apptID);
	channelMap.put("Appointment RecordNum",recordNum);
} else { // the appointment doesn't exist - a new one must be created
	var apptID = msgApptID;
	var nextsql = "declare @r varchar(25);exec spSpark_GetNextRecordNum 'APPT6',@recordnum = @r output;select @r as nextRecordNum;";
	var nextresults = SQLSelect(nextsql);
	nextresults.next(); // executeCachedQuery starts at row 0 of the ResultSet - so we advance to row 1
	var nextRecordNum = nextresults.getString('nextRecordNum');
	logger.info("SIU: NextRecordNum = " + nextRecordNum);
	channelMap.put("Appointment RecordNum",nextRecordNum);
	channelMap.put("Appointment ID",apptID);
}

var sql = "declare @r varchar(25);exec spSpark_GetNextUID '1059',@recordnum = @r output;select @r as nextUID;";

var results = SQLSelect(sql);

results.next(); // executeCachedQuery starts at row 0 of the ResultSet - so we advance to row 1

var apptUID = results.getString('nextUID');

logger.info("SIU: Appointment UID = " + apptUID);

channelMap.put('Appointment UID', apptUID);

// Gets the room name that the dentist's roster is in from the BLOCK6 table 
var sql = "with roster as (select dateadd(minute, wstarttime,dtdate) as starttm,dateadd(minute,wLength,dateadd(minute, wstarttime,dtdate)) as endtm,*" +
"from block6 where dtdate = '" + $('Appointment Date') + "' and wtype = 3 and providercode = '" + $('Doctor ID') + "') " + 
"select room from roster where cast('" + $('Appointment Date') + "' as datetime) + cast('" + $('Appointment Time') + "' as time) between starttm and endtm;"

var results = SQLSelect(sql);
if (results.next()) { // executeCachedQuery starts at row 0 of the ResultSet - so we advance to row 1
	var room = results.getString('room');
} else {
	var room = "";
}

channelMap.put("Room",room);
logger.info("SIU: Room = " + room.toString());

var msgApptID = msg['SCH']['SCH.1']['SCH.1.1'].toString();
logger.info("SIU: External ID from message = " + msgApptID );

var sql = "SELECT recordnum,ssExternalID from APPT6 where ssExternalID = '" + msgApptID + "'";

var results = SQLSelect(sql);

if (results.next()) { // executeCachedQuery starts at row 0 of the ResultSet - so we advance to row 1
	var apptID = results.getString('ssExternalID');
} else {
	var apptID = msgApptID;
	logger.info("SIU: appt id not in here");
}

channelMap.put("Appointment ID",apptID);

