/**
	Perform an SQL query on the Titanium database

	@param {Any} sql - 
	@return {resultset} results of query
*/
function SQLSelect(sql) {
	//connection string in the format: jdbc:sqlserver://servername:port;databasename=dbname
	var sqlserver = 'jdbc:sqlserver://' + configurationMap.get('Titanium Database Server') + 
	";databaseName=" + configurationMap.get('Titanium Database Name') + ";ssl=require;";

	var dbConn = DatabaseConnectionFactory.createDatabaseConnection(	
		'com.microsoft.sqlserver.jdbc.SQLServerDriver',
		sqlserver,	
		configurationMap.get('Titanium Username'),	
		configurationMap.get('Titanium Password')
		);

	var results = dbConn.executeCachedQuery(sql);
	dbConn.close();
	return results;
}

/**
	Perform an update query on the Titanium database

	@param {String} sql - SQL update query
	@return {resultset} returns a resultset
*/
function SQLUpdate(sql) {
	//connection string in the format: jdbc:jtds:sqlserver://servername:port/dbname
	var sqlserver = 'jdbc:sqlserver://' + configurationMap.get('Titanium Database Server') + 
	";databaseName=" + configurationMap.get('Titanium Database Name') + ";ssl=require;";
	//logger.info("update");

	var dbConn = DatabaseConnectionFactory.createDatabaseConnection(	
		'com.microsoft.sqlserver.jdbc.SQLServerDriver',
		sqlserver,	
		configurationMap.get('Titanium Username'),	
		configurationMap.get('Titanium Password')
		);

    	var results = dbConn.executeUpdate(sql);
    	dbConn.close();
    	return results;
}
