var sql = "SELECT ssExternalID from APPT6 where ssExternalID = '" + msgApptID + "'";

var results = dbConn.executeCachedQuery(sql);

if (results.next()) { // executeCachedQuery starts at row 0 of the ResultSet - so we advance to row 1
	var apptID = results.getString('ssExternalID');
} else {
	var apptID = msgApptID;
}

channelMap.put("Appointment ID",apptID);

dbConn.close();

// get the login cookie from the http response

resp = $('responseHeaders').getHeaderList('Set-Cookie');
//logger.info("cookie = " + resp);

// format it properly 
var array = resp.toString().split(";");
var cookie = "";

for each (var part in array) {
	var c = part.toString().replace("[","").replace("]","").replace(" HttpOnly, ","");
	if (c.toString().substr(0,7) == "ASP.NET") {
		cookie += c.toString();
		cookie += "; "
	}
	if (c.toString().substr(0,9) == "LoginPage")  {
		cookie += c.toString();
	}

}
//logger.info("final cookie = " + cookie);

channelMap.put('Cookie',cookie);

// responseStatusLine is the HTTP response code
// I've hardcoded our error case, but you can find other codes at
// http://www.tutorialspoint.com/http/http_responses.htm
if ($('responseStatusLine') == "HTTP/1.1 401 Unauthorized") {
	// Log the error and/or set the msg to a detailed description

	// >>> This is the critical step.  In an error case, set to QUEUED
	responseStatus = QUEUED;
	// This section could be repeated for additional codes
} else {
	// This section could check for specific (e.g. 200) codes.
	// In our case, the success message and other errors are all
	// JSON formatted so we lump them together here to share
	// decoding code
	var responseMessage = response.getMessage();
	var decodedReponseMessage = new java.lang.String(FileUtil.decode(responseMessage));
	msg = decodedReponseMessage;

	// We get back an encoded { "detail": "ok" } (w/ some newlines)
	// in the OK case.  Rather than dealing with newlines in the decoded 
	// version, we just look for the encoded version of the OK message.
	// You could test for any success condition here.
	if ($('responseStatusLine') != "HTTP/1.1 200 OK") {
		// >>> Again, critical step
		responseStatus = QUEUED;
	} else {
		// Probably unnecessary, but future-proofs the channel to 
		// changes in Mirth by being explicit.
		responseStatus = SENT
	}
}

